PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?c ?c_titulo ?cf ?cf_titulo ?ti ?termo where{
    ?c rdf:type :Classe_N3 .
    ?c :temFilho ?cf .
    ?c :temTI ?ti .
    ?ti :termo ?termo.
    ?c :titulo ?c_titulo .
    ?cf :titulo ?cf_titulo .

    FILTER (
        NOT EXISTS { ?cf :temTI ?ti . }
    )
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?c ?c_titulo ?cf ?cf_titulo where{
    ?c rdf:type :Classe_N3 .
    ?c :temFilho ?cf .
    ?c :titulo ?c_titulo .
    ?cf :titulo ?cf_titulo .

    FILTER (
        EXISTS { ?c :temDF ?df . } ||
        EXISTS { ?c :temPCA ?pca . }
    )
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select * where {
    ?c rdf:type :Classe_N3 .
    ?c :titulo ?c_titulo .

    FILTER (
        NOT EXISTS { ?c :temFilho ?cf . } &&
        (NOT EXISTS { ?c :temDF ?df . } ||
        NOT EXISTS { ?c :temPCA ?pca . })
    )
}

