PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
SELECT ?proc ?leg WHERE {
	?crit rdf:type :CriterioJustificacaoLegal.
	?crit :critTemLegAssoc ?leg .
	?just :temCriterio ?crit.
	{
		{
			?df :temJustificacao ?just.
			?proc :temDF ?df.
		} union {
			?pca :temJustificacao ?just.
			?proc :temPCA ?pca.
		}
	} MINUS {
		?proc :temLegislacao ?leg
	}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select distinct ?s ?s_titulo where {
    ?s clav:eSuplementoPara ?o .
    ?s :titulo ?s_titulo .

    minus {
        {
            ?s clav:temPCA ?pca.
            ?pca clav:temJustificacao ?pcajust.
            ?pcajust :temCriterio ?crit.
            ?crit rdf:type :CriterioJustificacaoUtilidadeAdministrativa
        }
        UNION
        {
            ?s clav:temFilho ?f
        }
    }
}

select ?s ?s_titulo ?o ?o_titulo where { 
?s rdf:type clav:Classe_N3.
?o rdf:type clav:Classe_N3.
?s clav:eSuplementoPara ?o .

?s clav:titulo ?s_titulo .
?o clav:titulo ?o_titulo .

minus {
{?s clav:temFilho ?f}
UNION
	{
?s clav:temPCA ?pca.
?pca clav:temJustificacao ?pcajust.
?pcajust clav:temCriterio ?crit.
?crit rdf:type clav:CriterioJustificacaoUtilidadeAdministrativa.
?crit clav:critTemProcRel ?o
}
}
}

