select ?s ?s_titulo ?o ?o_titulo where { 
    {
        {
            ?s clav:eSinteseDe ?o .
            ?s :titulo ?s_titulo .
            ?o :titulo ?o_titulo .
        }
        union
        {
            ?s clav:eSintetizadoPor ?o .
            ?s :titulo ?s_titulo .
