PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select * where {
    ?c1 rdf:type :Classe_N1 .

    minus {
        ?c1 :pertenceLC ?lc1 .
    }

    ?c2 rdf:type :Classe_N2 .
    ?c2 :temPai ?c1 .

    ?c3 rdf:type :Classe_N3 .
    ?c3 :temPai ?c2 .

    ?c4 rdf:type :Classe_N4.
    ?c4 :temPai ?c3 .

    FILTER (
        ?c1 != ?c2
        && ?c2 != ?c3
        && ?c3 != ?c4 &&
        (EXISTS { ?c2 :pertenceLC ?lc2 . }
        || EXISTS { ?c3 :pertenceLC ?lc3 . }
        || EXISTS { ?c4 :pertenceLC ?lc4 . })
    )
}

