select ?s where { 
    ?s :eSinteseDe ?o .

    minus { ?s :temDF ?df.
            ?df :dfValor "C" }
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s where { 
    ?s :eSintetizadoPor ?o .

    minus { ?s :temDF ?df.
            ?df :dfValor "E" }
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select distinct ?s where { 
    {{?s clav:eSinteseDe ?o .}
    union
        {?s clav:eSintetizadoPor ?o .}}
    minus
    {{    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
        ?crit rdf:type :CriterioJustificacaoDensidadeInfo}
     UNION
        {?s clav:temFilho ?f}}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
    {{?s clav:eSinteseDe ?o .}
    union
        {?s clav:eSintetizadoPor ?o .}}
    minus
    {    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
      ?crit rdf:type :CriterioJustificacaoDensidadeInfo.
        ?crit :temProcessoRelacionado ?o}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select distinct ?s where { 
?s rdf:type :Classe_N3 .
?s :eComplementarDe ?o .

    minus { ?s :temDF ?df.
            ?df :dfValor "C" }
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
    {?s rdf:type :Classe_N3.
     ?o rdf:type :Classe_N3.
     ?s clav:eComplementarDe ?o .}
    minus
    {    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
        ?crit rdf:type :CriterioJustificacaoComplementaridadeInfo}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
    {?s rdf:type :Classe_N3.
     ?o rdf:type :Classe_N3.
     ?s clav:eComplementarDe ?o .}
    minus
    {    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
        ?crit rdf:type :CriterioJustificacaoComplementaridadeInfo.
        ?crit :temProcessoRelacionado ?o }
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s where { 
    ?s :temDF ?d.
    ?d :temJustificacao ?dfjust.
    minus {
        {?dfjust :temCriterio ?crit.
           ?crit rdf:type :CriterioJustificacaoDensidadeInfo.}
        union {
            ?dfjust :temCriterio ?crit.
            ?crit rdf:type :CriterioJustificacaoLegal.
        }
        union{
            ?dfjust :temCriterio ?crit.
            ?crit rdf:type :CriterioJustificacaoComplementaridadeInfo
        }
    }
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select * where{
    ?c rdf:type :Classe_N3 .
    ?c :eComplementarDe ?o .
    ?c :temDF ?df .
    ?df :dfValor ?dfv .

    minus {
        ?c :temFilho ?f .
    }

    FILTER ( ?dfv != "C" )
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select * where {
    ?c rdf:type :Classe_N3 .
    ?c :eSinteseDe ?ca .
    ?c :temDF ?df .
    ?df :dfValor ?dfv .

    minus {
        ?c :temFilho ?cf .
        ?c :eComplementarDe ?cb .
    }

    FILTER ( ?dfv != "C" )
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select * where{
    ?c rdf:type :Classe_N3 .
    ?c :eSintetizadoPor ?ca .
    ?c :temDF ?df .
    ?df :dfValor ?dfv .

    minus {
        ?c :temFilho ?f .
        ?c :eComplementarDe ?cb .
        ?c :eSinteseDe ?cc .
    }

    FILTER ( ?dfv != "E" )
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select * where{
    ?c rdf:type :Classe_N3 .
    ?c :temDF ?df .
    ?df :dfValor ?dfv .

    minus {
        ?c :temFilho ?cf .
        ?c :eComplementarDe ?ca .
        ?c :eSintetizadoPor ?cb .
        ?c :eSinteseDe ?cc .
    }

    FILTER ( ?dfv != "NE" )
}
