PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s where {
    ?s clav:eSuplementoPara ?o .

    minus {{?s clav:temPCA ?pca.
           ?pca clav:temJustificacao ?pcajust.
            ?pcajust :temCriterio ?crit.
            ?crit rdf:type :CriterioJustificacaoUtilidadeAdministrativa}
         UNION
            {?s clav:temFilho ?f}}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
?s rdf:type :Classe_N3.
?o rdf:type :Classe_N3.
?s clav:eSuplementoPara ?o .

minus {
?s clav:temPCA ?pca.
        ?pca clav:temJustificacao ?pcajust.
        ?pcajust :temCriterio ?crit.
      ?crit rdf:type :CriterioJustificacaoUtilidadeAdministrativa.
        ?crit :temProcessoRelacionado ?o}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
    ?s rdf:type :Classe_N3.
    ?o rdf:type :Classe_N3.
    ?s clav:eSuplementoDe ?o .

minus {?s clav:temPCA ?pca.
                ?pca clav:temJustificacao ?pcajust.
             ?pcajust :temCriterio ?crit.
             ?crit :temProcessoRelacionado ?o}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select distinct ?s where { 
    {{?s clav:eSinteseDe ?o .}
    union
        {?s clav:eSintetizadoPor ?o .}}
    minus
    {{    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
        ?crit rdf:type :CriterioJustificacaoDensidadeInfo}
     UNION
        {?s clav:temFilho ?f}}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
    {{?s clav:eSinteseDe ?o .}
    union
        {?s clav:eSintetizadoPor ?o .}}
    minus
    {    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
      ?crit rdf:type :CriterioJustificacaoDensidadeInfo.
        ?crit :temProcessoRelacionado ?o}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select distinct ?s where { 
?s rdf:type :Classe_N3 .
?s :eComplementarDe ?o .

    minus { ?s :temDF ?df.
            ?df :dfValor "C" }
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
    {?s rdf:type :Classe_N3.
     ?o rdf:type :Classe_N3.
     ?s clav:eComplementarDe ?o .}
    minus
    {    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
        ?crit rdf:type :CriterioJustificacaoComplementaridadeInfo}
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s ?o where { 
    {?s rdf:type :Classe_N3.
     ?o rdf:type :Classe_N3.
     ?s clav:eComplementarDe ?o .}
    minus
    {    ?s :temDF ?df.
        ?df :temJustificacao ?dfjust.
         ?dfjust :temCriterio ?crit.
        ?crit rdf:type :CriterioJustificacaoComplementaridadeInfo.
        ?crit :temProcessoRelacionado ?o }
}

PREFIX : <http://jcr.di.uminho.pt/m51-clav#>
select ?s where { 
    ?s :temDF ?d.
    ?d :temJustificacao ?dfjust.
    minus {
        {?dfjust :temCriterio ?crit.
           ?crit rdf:type :CriterioJustificacaoDensidadeInfo.}
        union {
            ?dfjust :temCriterio ?crit.
            ?crit rdf:type :CriterioJustificacaoLegal.
        }
        union{
            ?dfjust :temCriterio ?crit.
            ?crit rdf:type :CriterioJustificacaoComplementaridadeInfo
        }
    }
}
